 CREATE OR REPLACE FUNCTION public.normalize_supplier_name(supplier_name text)                                                                                                                                                            +
  RETURNS text                                                                                                                                                                                                                            +
  LANGUAGE plpgsql                                                                                                                                                                                                                        +
 AS $function$                                                                                                                                                                                                                            +
 BEGIN                                                                                                                                                                                                                                    +
